import React, { Component } from 'react';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SideDrawer from './SideDrawer';
import Typography from '@material-ui/core/Typography';


class Header extends Component {

       state = { 
           drawerOpen: false,
           headerShow: false
             }

        componentDidMount() {
               window.addEventListener('scroll',this.handleScroll); 
             }

    handleScroll = () => {

if(window.scrollY > 0){
    this.setState ({
        headerShow:true
    })
}
 else {
    this.setState ({
        headerShow:false
    })

 }
    }

    toggleDrawer = (value) => {
        this.setState({
    drawerOpen: value
        })
    }


    render() {
        return (
            <div>

            <AppBar 
            position="fixed"
            style={{
                backgroundColor: this.state.headerShow ? '#363636' : 'transparent' ,
                boxShadow:'none',
                padding:'10px 0px' }}>
        <Toolbar>
            <div style={{flexGrow:1}} className='mb-4'>
               <div className='text-teal-500' > Shaurya </div>
               <div className='text-teal-500' >Tuli </div>
            </div>
               <IconButton color='primary' aria-label='Menu' onClick={()=> this.toggleDrawer(true)}> 
                 <MenuIcon/>
             </IconButton>
              <SideDrawer
                   open={this.state.drawerOpen}
                  onClose={(value)=> this.toggleDrawer(value)}/>
       </Toolbar>
            </AppBar>   
            </div>
        );
    }
}

export default Header;
