import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import { makeStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import { Typography } from '@material-ui/core';


function ElevationScroll(props) {
    const { children, window } = props;
    // Note that you normally won't need to set the window ref as useScrollTrigger
    // will default to window.
    // This is only being set here because the demo is in an iframe.
    const trigger = useScrollTrigger({
      disableHysteresis: true,
      threshold: 0,
      target: window ? window() : undefined,
    });

    return React.cloneElement(children, {
      elevation: trigger ? 4 : 0,
    });
  }


const useStyles = makeStyles(theme => ({

  toolbarMargin : {
    ...theme.mixins.toolbar,
    marginBottom: "3em",
  },

  logo : {
    height: "7rem"
  },

  tabsContainer : {
    marginLeft: "auto",
  },

  tabs : {
   ...theme.typography.tabs,
    minWidth: "10",
    marginLeft: "20px"
  }
}))

export default function Header(props) {
  const classes = useStyles()   

        return (
            <React.Fragment>
        <ElevationScroll>
          <AppBar>
            <Toolbar disableGutters> 
               <Typography>Shaurya Tuli</Typography>
                  <Tabs className={classes.tabsContainer}>
                    <Tab label="Home" className={classes.tabs}/>
                    <Tab label="Services" className={classes.tabs}/>
                    <Tab label="The Revolution" className={classes.tabs}/>
                    <Tab label="About Us" className={classes.tabs}/>
                    <Tab label="Contact Us" className={classes.tabs}/>
                 </Tabs>
            </Toolbar>
          </AppBar>
       </ElevationScroll>
       <div className={classes.toolbarMargin}/>
       </React.Fragment>
        );

}
